sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;

	var records = [];

	c.columns = JSON.parse(c.data.columns);
	records = JSON.parse(c.data.records);

	$scope.totalItems = records.length;
  $scope.currentPage = 1;
  $scope.itemsPerPage = parseInt(c.options.items_per_page);
	$scope.maxSize = parseInt(c.options.max_size);
	$scope.boundaryLinks = c.options.boundary_links === 'true' ? true : false;
	$scope.directionLinks = c.options.direction_links === 'true' ? true : false;
	$scope.rotate = c.options.rotate === 'true' ? true : false;

	$scope.$watch("currentPage", function() { 
    setPagingData($scope.currentPage);
  });

  function setPagingData(page) {

    var pagedData = records.slice(
      (page - 1) * $scope.itemsPerPage,
      page * $scope.itemsPerPage
    );

    c.aRecords = pagedData;
  }
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.records = '';
	data.columns = '';

	loadColums(options.table, options.field_list);
	loadRecords(options.table, options.field_list);

	function loadColums(tableParameter, fieldsString) {

		var table = '';
		var fields = [];
		var columns = [];

		table = tableParameter;
		fields = fieldsString.split(',');

		for (var field of fields) {

			var column = '';

			column = new GlideRecord(table).getElement(field).getLabel();

			columns.push(column);
		}

		data.columns = JSON.stringify(columns);	
	}

	function loadRecords(tableParameter, fieldsString) {

		var table = '';
		var fields = [];
		var records = [];
		var glideRecord;

		table = tableParameter;
		fields = fieldsString.split(',');

		glideRecord = new GlideRecord(table);		
		glideRecord.query();

		while (glideRecord.next()) {

			var record = {};

			for (var field of fields) {
				record[field] = glideRecord.getValue(field);
			}

			records.push(record);
		}

		data.records = JSON.stringify(records);
	}
})();
