sp_widget.client_script:
api.controller=function($scope) {
	/* widget controller */
	var c = this;

	var images = JSON.parse(c.data.system_property_images);

	$scope.myInterval = c.data.interval;
	$scope.noWrapSlides = false;
	$scope.slides = [];

	var currIndex = 0;
	for (var i = 0; i < images.length; i++) {
		$scope.slides.push({
			image: images[i].name,
			description: images[i].description,
			id: currIndex++
		});
	}
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	var system_property_images = options.system_property_images || 'x_821667_my_widget.mywidgets.image.carrousel';

	data.system_property_images = gs.getProperty(system_property_images);
	data.interval = options.interval_carrousel || 5000; // Or 5000 milliseconds

})();
