sp_widget.script:
(function() {

	var propertyName = 'x_821667_my_widget.gbd.main.news.items'; // change if you prefer another property name
	var itemsString = gs.getProperty(propertyName);
	var itemsJSON = JSON.parse(itemsString);
	var items = [];

	itemsJSON.forEach(function(item) {
		items.push({
			title: item.title,
			url: item.url,
			image: item.image,
			category: gs.getMessage(item.category)
		});
	});

	data.items = items;

})();
