sp_widget.script:
(function() {

	var profilesString = gs.getProperty('x_821667_my_widget.gbd-profile-carousel.profiles');
	console.log(profilesString);
	var profilesJSON = JSON.parse(profilesString);
	var profiles = [];

	profilesJSON.forEach(function(profile) {
		profiles.push({
			title: gs.getMessage(profile.title),
			url: profile.url,
			image: profile.image,
			alt: gs.getMessage(profile.alt)
		});
	});

	data.profiles = profiles;

  // Link "All Profiles"
  data.allProfilesUrl = 'https://www.gov.br/pt-br/perfil_usuario'; // [7](https://www.gov.br/pt-br/perfil_usuario)
})();
